import React from "react";
import Index from "./Home/Index";
import Driver from "./Components/Driver";
import Rider from "./Components/Rider";
import { BrowserRouter, Switch, Route } from "react-router-dom";
import About from "./Components/About";
import Create from "./Components/Create";
import Header from "./Components/Header";
import Join from "./Components/Join";
import JoinRide from "./Components/JoinRide";
import Login from "./Components/Login";
import Offer from "./Components/Offer";
import Req from "./Components/Req";
import Signup from "./Components/Signup";
import View from "./Components/View";
import ViewRides from "./Components/ViewRides";
import RideReq from "./Components/RideReq";

function App() {
  return (
    <BrowserRouter>
      <Switch>
        <Route exact to="/" component={Index} />
        <Route to="/about" component={About} />
        <Route to="/create" component={Create} />
        <Route to="/driver" component={Driver} />
        <Route to="/header" component={Header} />
        <Route to="/join" component={Join} />
        <Route to="/joinRide" component={JoinRide} />
        <Route to="/login" component={Login} />
        <Route to="/offer" component={Offer} />
        <Route to="/req" component={Req} />
        <Route to="/rider" component={Rider} />
        <Route to="/signup" component={Signup} />
        <Route to="/view" component={View} />
        <Route to="/viewRides" component={ViewRides} />
        <Route to="/ride" component={RideReq} />
      </Switch>
    </BrowserRouter>
  );
}

export default App;
