import React from "react";
import Index from "./Home/Index";
import Driver from "./Components/Driver";
import Rider from "./Components/Rider";
import RideReq from "./Components/RideReq";
import { BrowserRouter, Switch, Route } from "react-router-dom";
import About from "./Components/About";

function App() {
  return (
    <BrowserRouter>
      <Switch>
        <Route exact to="/" component={Index} />
        <Route exact to="/about" component={About} />
        <Route exact to="/create" component={Create} />
        <Route exact to="/driver" component={Driver} />
        <Route exact to="/header" component={Header} />
        <Route exact to="/input" component={Input} />
        <Route exact to="/join" component={Join} />
        <Route exact to="/joinRide" component={JoinRide} />
        <Route exact to="/login" component={Login} />
        <Route exact to="/offer" component={Offer} />
        <Route exact to="/req" component={Req} />
        <Route exact to="/rider" component={Rider} />
        <Route exact to="/signup" component={Signup} />
        <Route exact to="/view" component={View} />
        <Route exact to="/viewRide" component={ViewRide} />
        <Route exact to="/ridereq" component={RideReq} />
      </Switch>
    </BrowserRouter>
  );
}

export default App;
