import React, { Component } from "react";
import { HashLink } from "react-router-hash-link";
import { Switch, BrowserRouter, Route, Link } from "react-router-dom";
import Typist from "react-typist";
import StickyFooter from "react-sticky-footer";
import "./style.css";
import "../fonts.css";
import Header from '../Components/Header';
import About from '../Components/About';
import Login from '../Components/Login';
import Signup from '../Components/Signup';


function Index() {
  return (
    <div className="body1">
      <section id="header">
        <BrowserRouter>
        <div>
                <div className="Links" id="links">
                  <HashLink smooth to="/#header" className="hashbrand">
                    <font size="6">Ride My Way</font>
                  </HashLink>
                  <HashLink smooth to="/#header" exact className="hashlink">
                    Home
                  </HashLink>
                  <HashLink smooth to="/#about" className="hashlink">
                    About us
                  </HashLink>
                  <HashLink smooth to="/#login" className="hashlink">
                    Log in
                  </HashLink>
                  <HashLink smooth to="/#signup" className="hashlink">
                    Sign up
                  </HashLink>
                </div>
              </div>
          <Switch>
            <Route exact path="/#header" c>
              
            </Route>
          </Switch>
        </BrowserRouter>
        
      </section>

      

      

      

      <StickyFooter
        bottomThreshold={50}
        normalStyles={{
          backgroundColor: "#000",
          padding: "2rem",
          color: "#f7751f"
        }}
      >
        <div>Copyright &copy; 2019 Ride My Way</div>
      </StickyFooter>
    </div>
  );
}

export default Index;
